move(direction) {
    let moved = false;
    switch (direction) {
        case 'up':
            for (let c = 0; c < 4; c++) {
                for (let r = 1; r < 4; r++) {
                    if (this.board[r][c] !== 0) {
                        let row = r;
                        while (row > 0 && this.board[row - 1][c] === 0) {
