                            this.board[row][c] = 0;
                            this.score += this.board[row - 1][c];
                            moved = true;
                        }
                    }
                }
            }
            break;
        case 'down':
            for (let c = 0; c < 4; c++) {
                for (let r = 2; r >= 0; r--) {
                    if (this.board[r][c] !== 0) {
                        let row = r;
                        while (row < 3 && this.board[row + 1][c] === 0) {
                            this.board[row + 1][c] = this.board[row][c];
                            this.board[row][c] = 0;
