                            this.board[row][c] = 0;
                            this.score += this.board[row + 1][c];
                            moved = true;
                        }
                    }
                }
            }
            break;
        case 'left':
            for (let r = 0; r < 4; r++) {
                for (let c = 1; c < 4; c++) {
                    if (this.board[r][c] !== 0) {
                        let col = c;
                        while (col > 0 && this.board[r][col - 1] === 0) {
                            this.board[r][col - 1] = this.board[r][col];
                            this.board[r][col] = 0;
                            col--;
                            moved = true;
                        }
                        if (col > 0 && this.board[r][col - 1] === this.board[r][col]) {
                            this.board[r][col - 1] *= 2;
                            this.board[r][col] = 0;
                            this.score += this.board[r][col - 1];
                            moved = true;
                        }
                    }
                }
            }
            break;
        case 'right':
            for (let r = 0; r < 4; r++) {
                for (let c = 2; c >= 0; c--) {
                    if (this.board[r][c] !== 0) {
                        let col = c;
                        while (col < 3 && this.board[r][col + 1] === 0) {
                            this.board[r][col + 1] = this.board[r][col];
