#
# directx_x_file_parser.py
#
# Parses .x files used for DirectX.
# Based on format documentation at http://paulbourke.net/dataformats/directx/
#
# Copyright 2024, Paul McGuire
#
import pyparsing as pp


LBRACE, RBRACE, LBRACK, RBRACK, SEMI = pp.Suppress.using_each("{}[];")

ident = pp.Word(pp.alphas, pp.alphanums + "_").set_name("identifier")
integer = pp.Word("123456789", pp.nums).add_parse_action(lambda t: int(t[0]))

# scalar_type = pp.one_of(
#     "WORD DWORD FLOAT DOUBLE CHAR UCHAR BYTE STRING CSTRING UNICODE", as_keyword=True
# ).set_name("base_type")
scalar_type = pp.MatchFirst(
    pp.Keyword.using_each(
        "WORD DWORD FLOAT DOUBLE CHAR UCHAR BYTE STRING CSTRING UNICODE".split()
    )
).set_name("scalar_type")
type_ref = scalar_type | ident

ARRAY = pp.Keyword("array")
array_type_ref = pp.Group(ARRAY + type_ref("element_type"))
array_dim = LBRACK + (integer | ident) + RBRACK
member_defn = pp.Group(
    (
        array_type_ref("type") + ident("name") + array_dim[...]("dims")
        | type_ref("type") + ident("name")
    )
    + SEMI
)

TEMPLATE = pp.Keyword("template")
uuid = pp.Regex(
    r"<[0-9a-fA-F]{8}(-[0-9a-fA-F]{4}){3}-[0-9a-fA-F]{12}>"
).set_parse_action(lambda t: t[0][1:-1])
open_template_indicator = pp.Combine(LBRACK + "..." + RBRACK, adjacent=False)
restriction = pp.Group(type_ref("type") + pp.Optional(uuid)("uuid"))
template_restrictions = LBRACK + pp.DelimitedList(restriction) + RBRACK
directx_template_defn = (
    TEMPLATE
    + ident("name")
    + LBRACE
    + pp.Optional(uuid)("uuid")
    + member_defn[...]("members")
    + pp.Optional(
        open_template_indicator.set_parse_action(lambda: True), default=False
    )("open_template")
    + pp.Optional(template_restrictions)("restrictions")
    + RBRACE
).set_name("template_defn")
directx_template_defn.add_parse_action(
    lambda t: t.__setitem__("closed", not (t.open_template or t.restrictions))
)

directx_template_defn.ignore(pp.cpp_style_comment)


def make_template_parser(template_defn: pp.ParseResults) -> pp.ParserElement:
    """
    Create a pyparsing parser from a DirectX template definition.
    (Limited to templates containing scalar types, or arrays of scalars.)
    """
    float_ = pp.common.real
    type_map = {
        "WORD": integer,
        "DWORD": integer,
        "FLOAT": float_,
        "DOUBLE": float_,
        "CHAR": integer,
        "UCHAR": integer,
        "BYTE": integer,
        "STRING": pp.QuotedString('"'),
        "CSTRING": pp.QuotedString('"'),
        "UNICODE": pp.QuotedString('"'),
    }
    member_parsers = []
    for member in template_defn.members:
        if member.type in type_map:
            expr = pp.ungroup(type_map[member.type] + SEMI)
        elif member.dims:
            expr = type_map[member.type.element_type]
            for dim in member.dims:
                expr = pp.Group(pp.DelimitedList(expr, max=dim) + SEMI)
        member_parsers.append(expr(member.name))

    return (
        pp.Keyword(template_defn.name)("type")
        + ident("name")
        + LBRACE
        + pp.Group(pp.And(member_parsers))("fields")
        + RBRACE
    )


if __name__ == "__main__":

    sample = """
    some stuff...

    template Mesh {
    <3D82AB44-62DA-11cf-AB39-0020AF71E433>
    DWORD nVertices;
    array Vector vertices[nVertices];
    DWORD nFaces;
    array MeshFace faces[nFaces];
     [ ... ]                // An open template
    }

    template PolyArray {
    <3D82AB44-62DA-11cf-AB39-0020AF71E433>
    DWORD nPolys;
    array FLOAT polys[nPolys][3];
    }

    template Vector {
    <3D82AB5E-62DA-11cf-AB39-0020AF71E434>
    FLOAT x;
    FLOAT y;
    FLOAT z;
    }                        // A closed template

    template FileSystem {
    <3D82AB5E-62DA-11cf-AB39-0020AF71E435>
    STRING name;
    [ Directory <3D82AB5E-62DA-11cf-AB39-0020AF71E436>, File <3D82AB5E-62DA-11cf-AB39-0020AF71E437> ]    // A restricted template
    }

    more stuff...

    template mytemp {
    DWORD myvar;
    DWORD myvar2;
    }

    template container {
    DWORD count;
    array mytemp tempArray[count];
    }
    """

    for template in directx_template_defn.search_string(sample):
        # print(template.dump())
        print(
            f"Name: {template.name!r}"
            f" UUID: {template.uuid}"
            f" Open: {template.open_template!r}"
            f" Closed: {template.closed!r}"
            f" Restricted: {bool(template.restrictions)}"
        )
        # print()

    # create railroad diagram
    pp.autoname_elements()
    directx_template_defn.create_diagram(
        "directx_x_file_parser.html", show_results_names=True, show_groups=False
    )

    vector_template = directx_template_defn.parse_string(
        """\
    template Vector {
        <3D82AB5E-62DA-11cf-AB39-0020AF71E434>
        STRING label;
        FLOAT x;
        FLOAT y;
        FLOAT z;
    }  
    """
    )
    vector_parser = make_template_parser(vector_template)
    vector_parser.create_diagram(
        "directx_x_vector_parser.html", show_results_names=True, show_groups=False
    )
    v = vector_parser.parse_string('Vector p1 {"datum_A"; 1.0; 3.0; 5.0;}')
    print(v.dump())

    vector_template = directx_template_defn.parse_string(
        """\
    template Vector {
        <3D82AB5E-62DA-11cf-AB39-0020AF71E434>
        STRING label;
        array FLOAT coords[3];
    }  
    """
    )
    vector_parser = make_template_parser(vector_template)
    vector_parser.create_diagram(
        "directx_x_vector_parser.html", show_results_names=True, show_groups=False
    )
    v = vector_parser.parse_string('Vector p1 {"datum_A"; 1.0, 3.0, 5.0;}')
    print(v.dump())

examples/ebnf.py:
# This module tries to implement ISO 14977 standard with pyparsing.
# pyparsing version 1.1 or greater is required.

# ISO 14977 standardize The Extended Backus-Naur Form(EBNF) syntax.
# You can read a final draft version here:
# https://www.cl.cam.ac.uk/~mgk25/iso-ebnf.html
#
# Submitted 2004 by Seo Sanghyeon
#

from pyparsing import *


all_names = """
integer
meta_identifier
terminal_string
optional_sequence
repeated_sequence
grouped_sequence
syntactic_primary
syntactic_factor
syntactic_term
single_definition
definitions_list
syntax_rule
syntax
""".split()


integer = Word(nums)
meta_identifier = Word(alphas, alphanums + "_")
terminal_string = Suppress("'") + CharsNotIn("'") + Suppress("'") ^ Suppress(
    '"'
) + CharsNotIn('"') + Suppress('"')
definitions_list = Forward()
optional_sequence = Suppress("[") + definitions_list + Suppress("]")
repeated_sequence = Suppress("{") + definitions_list + Suppress("}")
grouped_sequence = Suppress("(") + definitions_list + Suppress(")")
syntactic_primary = (
    optional_sequence
    ^ repeated_sequence
    ^ grouped_sequence
    ^ meta_identifier
    ^ terminal_string
)
syntactic_factor = Optional(integer + Suppress("*")) + syntactic_primary
syntactic_term = syntactic_factor + Optional(Suppress("-") + syntactic_factor)
single_definition = delimitedList(syntactic_term, ",")
definitions_list << delimitedList(single_definition, "|")
syntax_rule = meta_identifier + Suppress("=") + definitions_list + Suppress(";")

ebnfComment = (
    ("(*" + ZeroOrMore(CharsNotIn("*") | ("*" + ~Literal(")"))) + "*)")
    .streamline()
    .setName("ebnfComment")
)

syntax = OneOrMore(syntax_rule)
syntax.ignore(ebnfComment)


def do_integer(str, loc, toks):
    return int(toks[0])


def do_meta_identifier(str, loc, toks):
    if toks[0] in symbol_table:
        return symbol_table[toks[0]]
    else:
        forward_count.value += 1
        symbol_table[toks[0]] = Forward()
        return symbol_table[toks[0]]


def do_terminal_string(str, loc, toks):
    return Literal(toks[0])


def do_optional_sequence(str, loc, toks):
    return Optional(toks[0])


def do_repeated_sequence(str, loc, toks):
    return ZeroOrMore(toks[0])


def do_grouped_sequence(str, loc, toks):
    return Group(toks[0])


def do_syntactic_primary(str, loc, toks):
    return toks[0]


def do_syntactic_factor(str, loc, toks):
    if len(toks) == 2:
        # integer * syntactic_primary
        return And([toks[1]] * toks[0])
    else:
        # syntactic_primary
        return [toks[0]]


def do_syntactic_term(str, loc, toks):
    if len(toks) == 2:
        # syntactic_factor - syntactic_factor
        return NotAny(toks[1]) + toks[0]
    else:
        # syntactic_factor
        return [toks[0]]


def do_single_definition(str, loc, toks):
    toks = toks.asList()
    if len(toks) > 1:
        # syntactic_term , syntactic_term , ...
        return And(toks)
    else:
        # syntactic_term
        return [toks[0]]


def do_definitions_list(str, loc, toks):
    toks = toks.asList()
    if len(toks) > 1:
        # single_definition | single_definition | ...
        return Or(toks)
    else:
        # single_definition
        return [toks[0]]


def do_syntax_rule(str, loc, toks):
    # meta_identifier = definitions_list ;
    assert toks[0].expr is None, "Duplicate definition"
    forward_count.value -= 1
    toks[0] << toks[1]
    return [toks[0]]


def do_syntax(str, loc, toks):
    # syntax_rule syntax_rule ...
    return symbol_table


symbol_table = {}


class forward_count:
    pass


forward_count.value = 0
for name in all_names:
    expr = vars()[name]
    action = vars()["do_" + name]
    expr.setName(name)
    expr.setParseAction(action)
    # ~ expr.setDebug()


def parse(ebnf, given_table={}):
    symbol_table.clear()
    symbol_table.update(given_table)
    forward_count.value = 0
    table = syntax.parseString(ebnf)[0]
    assert forward_count.value == 0, "Missing definition"
    for name in table:
        expr = table[name]
        expr.setName(name)
        # ~ expr.setDebug()
    return table
